#  GOOGLE SHEETS 
SHEET_NAME = "AAP New Hire Orientation Progress"
SCOPES = ["https://www.googleapis.com/auth/spreadsheets", "https://www.googleapis.com/auth/drive"]

@st.cache_resource
def get_gsheet_client():
    try:
        creds_dict = dict(st.secrets["gcp_service_account"])
        return gspread.service_account_from_dict(creds_dict)
    except Exception as e:
        st.error(f" Auth error: {e}")
        return None

def get_sheet():
    try:
        client = get_gsheet_client()
        if client is None:
            st.warning(" Google Sheets client failed to initialize.")
            return None
        return client.open(SHEET_NAME).sheet1
    except gspread.exceptions.SpreadsheetNotFound:
        st.warning(f" Spreadsheet \'{SHEET_NAME}\' not found. Check the name matches exactly and is shared with the service account.")
        return None
    except Exception as e:
        st.warning(f" Could not connect to Google Sheets: {type(e).__name__}: {e}")
        return None

def ensure_headers(sheet):
    try:
        if not sheet.row_values(1):
            sheet.append_row(["Trainee Name", "Module", "Module % Complete",
                               "Checklist Items Completed", "Quiz Score", "Last Updated"])
    except Exception:
        pass

def save_progress(username, module_key, module_title, checklist_items, quiz_results_for_module, module_pct):
    import json
    sheet = get_sheet()
    if not sheet:
        return
    ensure_headers(sheet)
    now = datetime.now().strftime("%Y-%m-%d %I:%M %p")
    quiz_correct = sum(1 for v in quiz_results_for_module.values() if v)
    quiz_total = len(quiz_results_for_module)
    # Store full checklist items and quiz results as JSON so we can restore them
    checklist_json = json.dumps(checklist_items)
    quiz_json = json.dumps({str(k): v for k, v in quiz_results_for_module.items()})
    row_data = [username, module_title, f"{module_pct}%",
                f"{len(checklist_items)} items checked", f"{quiz_correct}/{quiz_total}",
                now, checklist_json, quiz_json, module_key]
    try:
        all_rows = sheet.get_all_values()
        for i, row in enumerate(all_rows[1:], start=2):
            if len(row) >= 2 and row[0] == username and row[1] == module_title:
                sheet.update(f"A{i}:I{i}", [row_data])
                return
        sheet.append_row(row_data)
    except Exception as e:
        st.warning(f"Could not save progress: {e}")

def load_progress(username):
    """Load saved progress from sheet and restore into session state."""
    import json
    sheet = get_sheet()
    if not sheet:
        return False
    try:
        rows = sheet.get_all_values()
        restored_any = False
        for row in rows[1:]:
            if len(row) >= 2 and row[0] == username:
                module_key = row[8] if len(row) > 8 else None
                if not module_key:
                    continue
                # Restore checklist items
                if len(row) > 6 and row[6]:
                    try:
                        items = json.loads(row[6])
                        if items:
                            st.session_state.progress[module_key] = items
                            restored_any = True
                    except Exception:
                        pass
                # Restore quiz results
                if len(row) > 7 and row[7]:
                    try:
                        quiz = json.loads(row[7])
                        if quiz:
                            st.session_state.quiz_results[module_key] = {int(k): v for k, v in quiz.items()}
                            restored_any = True
                    except Exception:
                        pass
        return restored_any
    except Exception:
        return False

